import React, { Component } from 'react';
import Web3 from 'web3';
import './App.css';
import Ethergram from '../abis/Ethergram.json';
import Navbar from './Navbar';
import Main from './Main';

class App extends Component {
  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }
  async loadBlockchainData() {
    const web3 = window.web3

    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })

    const networkId = await web3.eth.net.getId()

    // Load Ethergram.sol
    const ethergramData = Ethergram.networks[networkId]
    if(ethergramData) {
      const ethergram = new web3.eth.Contract(Ethergram.abi, ethergramData.address)
      this.setState({ ethergram })
    } else {
      window.alert('Etherscan contract not deployed to detected network.')
    }
    console.log(this.state.account);

  }
  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  constructor(props) {
    super(props)
    this.state={
      account:'',
      ethergram:null,
      loading: true,
    }
  }
  render() {
    return (
      <div>
        <Navbar account={this.state.account} />
        { this.state.loading
          ? <div id="loader" className="text-center mt-5"><p>Loading...</p></div>
          : <Main

            />
        }
      </div>
    );
  }
}

export default App;
