import React, { Component } from 'react';
import Web3 from 'web3';
import './App.css';
import Ethergram from '../abis/Ethergram.json';
import Navbar from './Navbar';
import Main from './Main';

const ipfsClient=require('ipfs-http-client')
const ipfs = ipfsClient({host: 'ipfs.infura.io', port:5001, protocol: 'https'})

class App extends Component {
  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }
  async loadBlockchainData() {
    const web3 = window.web3

    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })

    const networkId = await web3.eth.net.getId()

    // Load Ethergram.sol
    const ethergramData = Ethergram.networks[networkId]
    if(ethergramData) {
      const ethergram = new web3.eth.Contract(Ethergram.abi, ethergramData.address)
      this.setState({ ethergram })
        //Load the images by 1st calling the imageCount() and add it to your state
     const imageCount = await ethergram.methods.imageCount().call()
     this.setState({imageCount})
     this.setState({loading: false})
    } else {
      window.alert('Etherscan contract not deployed to detected network.')
    }
  }
   captureFile=(event)=>{
      event.preventDefault()
      const file = event.target.files[0] 
      const reader = new window.FileReader()
      reader.readAsArrayBuffer(file)

      reader.onloadend=()=>{
        this.setState({buffer: Buffer(reader.result)})
        console.log('buffer', this.state.buffer)
      }
  }
  uploadImage = description => {
    console.log("Submitting file to ipfs...")

    //adding file to the IPFS
    ipfs.add(this.state.buffer, (error, result) => {
      console.log('Ipfs result', result)
      if(error) {
        console.error(error)
        return
      }

      this.setState({ loading: true })
      this.state.ethergram.methods.uploadImage(result[0].hash, description).send({ from: this.state.account }).on('transactionHash', (hash) => {
        this.setState({ loading: false })
      })
    })
  }




  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  constructor(props) {
    super(props)
    this.state={
      account:'',
      ethergram:null,
      images:[],
      loading: true,
      buffer:null
    }
  }
  render() {
    return (
      <div>
        <Navbar account={this.state.account} />
        { this.state.loading
          ? <div id="loader" className="text-center mt-5"><p>Loading...</p></div>
          : <Main
              captureFile={this.captureFile}
              uploadImage={this.uploadImage}
            />
        }
      </div>
    );
  }
}

export default App;
